WITH cte 
     AS (SELECT EmailId, NAATINumber 
         FROM   tblEmail 
                JOIN tblEntity 
                  ON tblEntity.EntityId = tblEmail.EntityId 
                JOIN tblPerson 
                  ON tblPerson.EntityId = tblEntity.EntityId) 
UPDATE tblEmail 
SET    Email = 'User'+CAST(NAATINumber as nvarchar(50)) +   CASE WHEN IsPreferredEmail = 1 THEN '' ELSE '_2' END + '@altf4solutions.com.au'
FROM   cte 
WHERE  cte.EmailId = tblEmail.EmailId;

